  INSERT INTO CARRERA (CARRERA_ID,NOMBRE,CLAVE,DESCRIPCION) VALUES (1,'INGENIERIA EN COMPUTACION',001,'LA CARRERA DE INGENIERO EN COMPUTACIÓN TIENE COMO OBJETIVO FORMAR PROFESIONALES CAPACES DE PLANEAR, DISEÑAR, ORGANIZAR, PRODUCIR, OPERAR Y DAR SOPORTE TÉCNICO A LOS SISTEMAS ELECTRÓNICOS PARA EL PROCESAMIENTO DE DATOS, A LOS SISTEMAS DE PROGRAMACIÓN -DE BASE Y DE APLICACIÓN DEL EQUIPO DE CÓMPUTO-, ASÍ COMO EFECTUAR EL CONTROL DIGITAL DE PROCESOS AUTOMÁTICOS.');
  INSERT INTO CARRERA (CARRERA_ID,NOMBRE,CLAVE,DESCRIPCION) VALUES (2,'INGENIERIA ELECTRICA',002,'LA CARRERA DE INGENIERÍA ELÉCTRICA ELECTRÓNICA TIENE COMO OBJETIVO FORMAR PROFESIONISTAS QUE UTILIZAN LAS CIENCIAS FÍSICAS, QUÍMICAS Y MATEMÁTICAS, ASÍ COMO TÉCNICAS DE INGENIERÍA, ECONOMÍA, COMPUTACIÓN Y ADMINISTRACIÓN PARA TRANSFORMAR LA NATURALEZA MEDIANTE DISPOSITIVOS MECÁNICOS, ELÉCTRICOS Y ELECTRÓNICOS EN BENEFICIO DE LA SOCIEDAD, Y PARA OPTIMAR LA INTEGRACIÓN Y EL FUNCIONAMIENTO DE LOS SISTEMAS PRODUCTIVOS FORMADOS POR HOMBRES, MÁQUINAS E INSUMOS.');
  INSERT INTO CARRERA (CARRERA_ID,NOMBRE,CLAVE,DESCRIPCION) VALUES (3,'INGENIERIA EN TELECOMUNICACIONES',003,'EL INGENIERO EN TELECOMUNICACIONES ES EL PROFESIONISTA QUE APLICA CONOCIMIENTOS DE LAS CIENCIAS BÁSICAS - FÍSICA Y MATEMÁTICAS-, ASÍ COMO LOS CORRESPONDIENTES A SEÑALES, SISTEMAS, ELECTRÓNICA Y COMPUTACIÓN PARA ANALIZAR, DISEÑAR, PLANEAR, ORGANIZAR, PRODUCIR, INSTALAR, DESARROLLAR, ADEMÁS DE MANTENER EN OPERACIÓN Y ADMINISTRAR REDES Y SISTEMAS DE TELECOMUNICACIONES.');
  INSERT INTO CARRERA (CARRERA_ID,NOMBRE,CLAVE,DESCRIPCION) VALUES (4,'INGENIERIA MECANICA',004,'LA CARRERA DE INGENIERÍA MECÁNICA FORMA PROFESIONALES QUE, MEDIANTE EL USO DE LA FÍSICA, LA QUÍMICA, LAS MATEMÁTICAS Y LAS TÉCNICAS DE INGENIERÍA, PROPORCIONAN A LA SOCIEDAD HERRAMIENTAS Y BIENES QUE LE PERMITEN APROVECHAR LOS RECURSOS NATURALES PARA SATISFACER LAS NECESIDADES MATERIALES, SOCIALES E INCLUSO INTELECTUALES DEL HOMBRE.');
  INSERT INTO CARRERA (CARRERA_ID,NOMBRE,CLAVE,DESCRIPCION) VALUES (5,'INGENIERIA CIVIL',005,'EL INGENIERO CIVIL ES EL PROFESIONISTA QUE POSEE UNA FORMACIÓN MULTIDISCIPLINARIA CONFORMADA POR CONOCIMIENTOS GENERALES DE MATEMÁTICAS, FÍSICA, QUÍMICA; Y DE LAS ÁREAS ESPECÍFICAS DE LA DISCIPLINA, LOS QUE AUNADOS A LAS NOCIONES EN COMPUTACIÓN, COMUNICACIÓN GRÁFICA, INFORMÁTICA, ADMINISTRACIÓN Y EVALUACIÓN DE PROYECTOS, LO CAPACITAN PARA PARTICIPAR EN LAS ETAPAS DE PLANEACIÓN, DISEÑO, ORGANIZACIÓN, CONSTRUCCIÓN, OPERACIÓN Y CONSERVACIÓN DE OBRAS CIVILES Y DE INFRAESTRUCTURA.');
